Castle.DynamicProxy.ProxyGenerationOptions options = new Castle.DynamicProxy.ProxyGenerationOptions();
options.AddMixinInstance(new MixinType());

Castle.DynamicProxy.ProxyGenerator gen = new Castle.DynamicProxy.ProxyGenerator();
object p = gen.CreateClassProxy(typeof(TargetType), options, new SimpleInterceptor());

((TargetType) p).SomeMethod();     // the interceptor worked
Assert.IsTrue(p is IMixinInterface); // failed.  MixinType has empty argument constructor and implement the IMixinInterface
