field.value:
following test fails when using open generics for registration: ObservableCollection<> , when ObservableCollection<int> is used test passes

[SetUp]
public void Setup()
{
_windsor = new WindsorContainer(); 
_windsor.Kernel.Register(Castle.MicroKernel.Registration.Component.For<TestInterceptor>().LifeStyle.Transient);
_windsor.Register(Castle.MicroKernel.Registration.Component.For(typeof (ObservableCollection<>))
.ImplementedBy(typeof(ObservableCollection<>))
.Proxy.AdditionalInterfaces(typeof (ITestInterface))
.Interceptors(new InterceptorReference(typeof (TestInterceptor))).Last
.LifeStyle.Transient
);
}

[Test]
public void Test1()
{
var tst = _windsor.Resolve<ObservableCollection<int>>();
var tst1 = tst as ITestInterface;
Assert.IsNotNull(tst1);
}
