Apologies for the bad summary name, I couldn't think of anything better.  Anyways, code speaks for itself.  Thanks.
{code lang=java}
        public class A
        {
            public int Id { get; set; }
            public Func<int> Func { get; set; }
            public A(int id, Func<int> func)
            {
                Id = id;
                Func = func;
            }
        }

        public interface IProvider
        {
            A Create(int id);
        }

        [Test]
        public void Func_should_be_resolved_from_typed_factory_provider()
        {
            container.Register(Component.For<A>());
            container.Register(Component.For<int>().Instance(123));
            container.Register(Component.For<IProvider>().AsFactory());
            Assert.That(container.Resolve<IProvider>().Create(1).Func(), Is.EqualTo(123)); // will throw a missing dependency exception

            // container.Resolve<Func<int>>();
            // if the func is resolved before IProvider is resolved, the test will pass
        }
{code}
