[Test]
public void ResolveAll_ForwardedHandlerForGenericType_Resolves()
{
	var container = new WindsorContainer();
	container.Register(Component.For<IOtherService, IGenericTest<object>>().ImplementedBy<GenericTest>());

	var services = container.ResolveAll<IGenericTest<object>>();

	Expect(services.Any(s => s is GenericTest));
}

private interface IGenericTest<T>
{
}

private interface IOtherService
{
}

public class GenericTest : IOtherService, IGenericTest<object>
{
}
