[Test]
public void OnCreate_should_invoke_for_open_generic_registrations()
{
	bool invoked = false;
	Container.Register(
		Component.For(typeof(Collection<>))
			.OnCreate(_ => invoked = true));

	Container.Resolve<Collection<A>>();
	Assert.IsTrue(invoked);
}
