Model 

    [ActiveRecord]
    public class FieldInstance : Base<FieldInstance>
    {
        private int _id;
        private EntityRef<FormInstance> _formInstance;
        private EntityRef<Field> _field;
        private string _value;

        public FieldInstance()
        {
            _formInstance = new EntityRef<FormInstance>(
                delegate(FormInstance fi) { fi.FieldInstances.Add(this); },
                delegate(FormInstance fi) { fi.FieldInstances.Remove(this); }
                );
            _field = new EntityRef<Field>();
        }

        [PrimaryKey]
        public int Id
        {
            get { return _id; }
            set { _id = value; }
        }

        [BelongsTo("FormInstanceId")]
        public FormInstance FormInstance
        {
            get { return _formInstance.Value; }
            set { _formInstance.Value = value; }
        }

        [BelongsTo("FieldId")]
        public Field Field
        {
            get { return _field.Value; }
            set { _field.Value = value; }
        }

        [Property]
        public string Value
        {
            get { return _value; }
            set { _value = value; }
        }

    }

FormInstance.FieldInstances[n].Id gets set for both, but FormInstance.FieldInstances[n].Value does not.
